import React, { useState } from 'react';
import './style.css';

function Box(props) {
  const [text, setText] = useState('');

  function toggleText() {
    if (text === '') {
      setText(props.currentState);
      props.changeTurn(props.id);
    }
  }

  return (
    <div className='box' onClick={toggleText}>
      {text}
    </div>
  );
}

export default Box;
